        private static ColorList BuildPalette()
        {
            var v = Palette.Split(';');
            List<Color> c = new List<Color>();
            foreach (var vv in v)
            {
                var d = vv.Split(',');
                c.Add(Color.FromArgb(int.Parse(d[0]), int.Parse(d[1]), int.Parse(d[2])));
            }
            return new ColorList
            {
                Colors = c.ToArray(),
                Tags = "",
            };
        }

        private static VoxelModelPart BuildPart(int parent, string bone, string str, Vector3 bp)
        {
            var ret = new VoxelModelPart
            {
                BoneName = bone,
                BasePoint = bp,
                Translation = new Vector3(),
                ParentId = parent,
            };
            List<VoxelData> voxelList = new List<VoxelData>();
            var v = str.Split(';');
            foreach (var vv in v)
            {
                var d = vv.Split(',');
                voxelList.Add(new VoxelData { X = -int.Parse(d[0]), Y = -int.Parse(d[1]), Z = int.Parse(d[2]), Color = int.Parse(d[3]) });
            }
            ret.VoxelList = voxelList.ToArray();
            return ret;
        }

        public static VoxelModel CreateModel()
        {
            return new VoxelModel
            {
                Name = "Hiyori",
                Author = "acaly",
                Comments = "The first VoxelModel for testing the editor.",
                Palette = new[] { BuildPalette() },
                Parts = new[]
                {
                    BuildPart(-1, "body", Part0, new Vector3(-20, -25, 0)),
                    BuildPart(0, "head", Part1, new Vector3(-20, -24.5f, 28)),
                    BuildPart(0, "left-arm", Part3, new Vector3(-20, -29, 25)),
                    BuildPart(0, "right-arm", Part2, new Vector3(-20, -20, 25)),
                    BuildPart(0, "left-leg", Part5, new Vector3(-21, -26, 18)),
                    BuildPart(0, "right-leg", Part4, new Vector3(-21, -23, 18)),
                    BuildPart(0, "debug", PartDebug, new Vector3(-20, -25, 45)),
                },
            };
        }
